// projects/sng-ui/src/lib/switch/sng-switch.ts
// import {
//   Component,
//   model,
//   input,
//   ChangeDetectionStrategy,
//   booleanAttribute,
//   computed,
// } from '@angular/core';
// import { cn } from './cn';
